import Pokemon from "../models/pokemon";

type Response = {
  message: string;
  data: any;
};

export default class PokemonService {
  static getPokemons(): Promise<Response> {
    return fetch("http://localhost:3001/api/pokemons?limit=20", {
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJpYXQiOjE3MDg4OTQ5NjgsImV4cCI6MTcwODk4MTM2OH0.Gdk5r5lWsy86P8eZXD5bs8PB_fQE2mt5Kao3IKyGnDo",
      },
    })
      .then((response) => response.json())
      .catch((error) => this.handleError(error));
  }

  static getPokemon(name: string): Promise<Response> {
    return fetch(`http://localhost:3001/api/pokemon/${name}`, {
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJpYXQiOjE3MDg4OTQ5NjgsImV4cCI6MTcwODk4MTM2OH0.Gdk5r5lWsy86P8eZXD5bs8PB_fQE2mt5Kao3IKyGnDo",
      },
    })
      .then((response) => response.json())
      .catch((error) => this.handleError(error));
  }

  static updatePokemon(pokemon: Pokemon): Promise<Response> {
    console.log(pokemon.id);
    return fetch(`http://localhost:3001/api/pokemon/${pokemon.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJpYXQiOjE3MDg4OTQ5NjgsImV4cCI6MTcwODk4MTM2OH0.Gdk5r5lWsy86P8eZXD5bs8PB_fQE2mt5Kao3IKyGnDo",
      },
      body: JSON.stringify(pokemon),
    })
      .then((response) => response.json())
      .then((pokemon) => {
        console.log(pokemon);
        return pokemon;
      })
      .catch((error) => this.handleError(error));
  }

  static handleError(error: Error): void {
    console.log(error);
  }

  // Permet de vérifier si l'objet est vide
  /*
  static isEmpty(data: Object): boolean {
    return Object.keys(data).length === 0;
  }
  */
}
