import { FunctionComponent, useState, useEffect } from "react";
import { Link, RouteComponentProps } from "react-router-dom";
import PokemonForm from "../components/pokemon-form";
import Pokemon from "../models/pokemon";

type Params = { name: string };

const PokemonEdit: FunctionComponent<RouteComponentProps<Params>> = ({
  match,
}) => {
  const [pokemon, setPokemon] = useState<Pokemon | null>(null);

  useEffect(() => {
    fetch(`http://localhost:3001/api/pokemon/${match.params.name}`, {
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJpYXQiOjE3MDg2MzM0ODYsImV4cCI6MTcwODcxOTg4Nn0.oAeq_bKVxPXpNhu6YsVXnfhT9-XMKtiJrZbxx5QZSu8",
      },
    })
      .then((response) => response.json())
      .then((response) => {
        if (response.data) {
          const pokemon: Pokemon = {
            ...response.data,
            ...{ created: new Date(response.data.created) },
          };
          setPokemon(pokemon);
        }
      });
  }, [match.params.name]);

  return (
    <div>
      {pokemon ? (
        <div className="row">
          <h2 className="header center">Éditer {pokemon.name}</h2>
          <PokemonForm pokemon={pokemon}></PokemonForm>
        </div>
      ) : (
        <div className="center">
          <h4 className="center">Ce pokémon n'existe pas !</h4>
          <Link to="/" className="waves-effect waves-teal btn-flat">
            Retour à la liste de pokémons
          </Link>
        </div>
      )}
    </div>
  );
};

export default PokemonEdit;
