import { FunctionComponent, useState } from "react";
import { BrowserRouter as Router, Link, Switch, Route } from "react-router-dom";
import PokemonList from "./pages/pokemon-list";
import PokemonAdd from "./pages/pokemon-add";
import PokemonsDetail from "./pages/pokemon-detail";
import PageNotFound from "./pages/page-not-found";
import PokemonEdit from "./pages/pokemon-edit";
import PageServerError from "./pages/page-server-error";

const App: FunctionComponent = () => {
  const [showAlert, setShowAlert] = useState(true);

  const closeAlert = () => {
    setShowAlert(false);
  };
  return (
    // BrowserRouter permet de mettre en place le système de navigation
    <Router>
      <div>
        {/* La barre de navigation commun à toutes les pages */}
        <nav>
          <div className="nav-wrapper teal">
            <Link to="/" className="brand-logo center">
              Pokédex
            </Link>
          </div>
        </nav>
        {showAlert && (
          <div className="row">
            <div className="col s12 m12">
              <div className="card red darken-1">
                <div className="row">
                  <div className="col s12 m10">
                    <div className="card-content white-text">
                      <p>1. Username cant be empty</p>
                    </div>
                  </div>
                  <div className="col s12 m2">
                    <i
                      className="fa fa-times"
                      aria-hidden="true"
                      style={{
                        position: "absolute",
                        right: "10px",
                        top: "10px",
                        fontSize: "20px",
                        color: "white",
                        cursor: "pointer",
                      }}
                      onClick={closeAlert}
                    >
                      X
                    </i>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
        {/* 
        Le système de gestion des routes de l'application
        Switch permet d'afficher le contenu d'une seule route à la fois
        */}
        <Switch>
          {/* 
          Route permet de décrire chaque route de l'application,
          exact permet de dire que l'URL doit correspondre exactement au chemin indiqué
          et qu'il ne s'agit pas d'un préfixe
          */}
          <Route exact path="/" component={PokemonList} />
          <Route exact path="/pokemons" component={PokemonList} />
          <Route exact path="/pokemons/add" component={PokemonAdd} />
          <Route exact path="/pokemons/edit/:name" component={PokemonEdit} />
          <Route path="/pokemons/:name" component={PokemonsDetail} />
          {/* 
          permet d'intercepter les routes inexistantes
          Attention, l'ordre de déclaration des routes est importante
          */}
          <Route component={PageNotFound} />
          <Route component={PageServerError} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
