import { FunctionComponent, useState, useEffect, useRef } from "react";
import { RouteComponentProps, Link } from "react-router-dom";
import Pokemon from "../models/pokemon";
import formatDate from "../helpers/format-date";
import formatType from "../helpers/format-type";
import PokemonService from "../services/pokemon-service";
import Loader from "../components/loader";

type Params = { name: string };

// Pour exploiter les paramètres reçues par le router => match
const PokemonsDetail: FunctionComponent<RouteComponentProps<Params>> = ({
  match,
}) => {
  const [pokemon, setPokemon] = useState<Pokemon | null>(null);

  const flag = useRef(false);

  useEffect(() => {
    if (flag.current === false) {
      PokemonService.getPokemon(match.params.name).then((response) => {
        if (response.data) {
          const pokemon: Pokemon = {
            ...response.data,
            ...{ created: new Date(response.data.created) },
          };
          setPokemon(pokemon);
          console.log(1);
          return () => (flag.current = true);
        }
      });
    }
  }, [match.params.name]);

  return (
    <div>
      {pokemon ? (
        <div className="row">
          <div className="col s12 m8 offset-m2">
            <h2 className="header center">{pokemon.name}</h2>
            <div className="card hoverable">
              <div className="card-image">
                <img
                  src={pokemon.picture}
                  alt={pokemon.name}
                  style={{ width: "250px", margin: "0 auto" }}
                />
                <Link
                  to={`/pokemons/edit/${pokemon.name}`}
                  className="btn btn-floating halfway-fab waves-effect waves-light"
                >
                  <i className="material-icons">edit</i>
                </Link>
              </div>
              <div className="card-stacked">
                <div className="card-content">
                  <table className="bordered striped">
                    <tbody>
                      <tr>
                        <td>Nom</td>
                        <td>
                          <strong>{pokemon.name}</strong>
                        </td>
                      </tr>
                      <tr>
                        <td>Points de vie</td>
                        <td>
                          <strong>{pokemon.hp}</strong>
                        </td>
                      </tr>
                      <tr>
                        <td>Dégâts</td>
                        <td>
                          <strong>{pokemon.cp}</strong>
                        </td>
                      </tr>
                      <tr>
                        <td>Types</td>
                        <td>
                          {pokemon.types.map((type) => (
                            <span key={type} className={formatType(type)}>
                              {type}
                            </span>
                          ))}
                        </td>
                      </tr>
                      <tr>
                        <td>Date de création</td>
                        <td>{formatDate(pokemon.created)}</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
                <div className="card-action">
                  <Link to="/">Retour</Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="center">
          <Loader />
        </div>
      )}
    </div>
  );
};

export default PokemonsDetail;
