type Response = {
  message: string;
  data: any;
};

export default class PokemonService {
  static getPokemons(): Promise<Response> {
    return fetch("http://localhost:3001/api/pokemons?limit=20", {
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJpYXQiOjE3MDg2MzM0ODYsImV4cCI6MTcwODcxOTg4Nn0.oAeq_bKVxPXpNhu6YsVXnfhT9-XMKtiJrZbxx5QZSu8",
      },
    }).then((response) => response.json());
  }

  static getPokemon(name: string): Promise<Response> {
    return fetch(`http://localhost:3001/api/pokemon/${name}`, {
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoxLCJpYXQiOjE3MDg2MzM0ODYsImV4cCI6MTcwODcxOTg4Nn0.oAeq_bKVxPXpNhu6YsVXnfhT9-XMKtiJrZbxx5QZSu8",
      },
    }).then((response) => response.json());
  }

  // Permet de vérifier si l'objet est vide
  /*
  static isEmpty(data: Object): boolean {
    return Object.keys(data).length === 0;
  }
  */
}
