import { FunctionComponent, useState } from "react";

type Props = {
  errorMsg: string;
};

const AlertError: FunctionComponent<Props> = ({ errorMsg }) => {
  const [showAlert, setShowAlert] = useState(true);

  const closeAlert = () => {
    setShowAlert(false);
  };

  return (
    <div>
      {/* pop-up alert commun Ã  toutes les pages */}
      {showAlert && (
        <div className="row">
          <div className="col s12 m12">
            <div className="card red darken-1">
              <div className="row">
                <div className="col s12 m10">
                  <div className="card-content white-text">
                    <p>{errorMsg}</p>
                  </div>
                </div>
                <div className="col s12 m2">
                  <i
                    className="material-icons"
                    style={{
                      position: "absolute",
                      right: "10px",
                      top: "10px",
                      fontSize: "20px",
                      color: "white",
                      cursor: "pointer",
                    }}
                    onClick={closeAlert}
                  >
                    close
                  </i>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AlertError;
